SELECT
        "src_ip",
        "src_mac",
        "dst_ip",
        "proto",
        "dst_port",
        "reviewed",
        count(*)
FROM
        "log"
WHERE
        (
                ("dst_sim" == "1")
        )
GROUP BY
      "src_ip",
      "src_mac",
      "dst_ip",
      "proto",
      "dst_port",
      "reviewed"
ORDER BY
      "src_ip"
;
